138 //**************************** Event handler  *****************************************
139 /* the original naming was CallKernekSendService, but
140 * inside each API we have to change the name. Otherwise
141 * C-compiler issues redefined function error.
142 */
143 void DisplayCallKernelSendService(char* message, FRAME_TYPE type, int length)
144 {
145         KERNEL_FRAME _frame;
146         //int index = 0;
147         _frame.F_SENDER = DISPLAY;
148         _frame.F_RECEIVER = DISPLAY;
149         _frame.F_TYPE = type;
150 
151         _frame.FRAME_MESSAGE = message;
152         _frame.MESSAGE_LENGTH = length;
153         _frame.F_PRIORITY = NORMAL_FRAME;
154         KL_SendFrame(&_frame);
155 }
156 
157 //**************************** Private functions **************************************
158 int _idx = 0;       // used inside for-loops
159 int result = 0;
160 
161 int GetCommandParameter(unsigned char cmd[])
162 {
163         unsigned char _digits[3];
164 
165         _digits[0] = cmd[14];   // read the first byte of the first digit
166         _digits[1] = cmd[16];   // read the first byte of the second digit
167         _digits[2] = cmd[18];   // read the first byte of the third digit
168 
169         result = atoi(_digits); // convert digits to the intiger
170 
171         return result;
172 }
173 
174 int _result = 0;
175 // Update KVP,mA,Time and JOG commands' parameters values
176 void UpdateCommandParameter(unsigned char cmd[], int step)
177 {
178         unsigned char _newValue[7];
179         memset(_newValue, '0', 7); // make sure temp. variable is clear
180 
181         _result = GetCommandParameter(cmd);
182         _result += step;         // step up/down the number
183 
184         intToStr(_result, _newValue);      // change the space for with zero for empty string
185 
186 
187         cmd[14] = _newValue[3];    // first digits new value
188         cmd[16] = _newValue[4];    // second digits new value
189         cmd[18] = _newValue[5];    // third digit new value
190 }
191 
192 int CheckMessageFinished(unsigned char* buffer, int mLen)
193 {
194 
195         if (mLen < 4)
196                 return 0;
197 
198         //frame tail : 0xCC,0x33,0xC3,0x3C
199         if (buffer[mLen - 4] == 0xCC && buffer[mLen - 3] == 0x33 &&
200                 buffer[mLen - 2] == 0xC3 && buffer[mLen - 1] == 0x3C)
201         {
202                 return 1; //true
203         }
204         return 0; // false
205 }
