255 unsigned char _selectPictureCopy[8];
256 int index = 0;int paramVal = 0;
257 void ProcessMessage(unsigned char* message)
258 {
259 
260         // example message to be processed: 0xAA 0x78 0x00 0x02 0xCC 0x33 0xC3 0x3C
261         unsigned char btncode = message[3]; // bytes 2 and 3 are pressed button code. \
262                                                                                                                            // in our application we use just byte 3
263         unsigned char cmdcode = message[1];
264 
265         switch (cmdcode)
266         {
267         case 0x7C:
268                 // user entered the password, check it here
269 
270                 if ((int)message[3] == 4) // password length is 4
271                 {
272                         // assume password is 2018 = 0x32 0x030 0x31 0x38
273                         if (message[4] == 0x32 && message[5] == 0x30
274                                 && message[6] == 0x312 && message[7] == 0x38)
275                         {
276                              // TODO: there is issue with api. I could not find a way to
277                              //       change the image here!!!
278                              //unsigned char _selectPictureCopy[8]; // backup the selectPicture
279                              //memcpy(_selectPictureCopy, selectPicture, 8);
280                              //_selectPictureCopy[3] = 0x0B; // successful login
281                              //DisplayCallKernelSendService(_selectPictureCopy, NORMAL_FRAME, 8);
282                         }
283 
284                 }
285                 else
286                 {
287                         // Show main window. 
288                         DisplayCallKernelSendService(selectPicture, NORMAL_FRAME, 8);
289                 }
290 
291                 break;
292 
293         case 0x78:
294                 // a button is pressed
295                 switch (btncode)
296                 {
297                 case 0x01:
298                         // go to automatic mode
299                         if (_automaticModeLst != 0)
300                                 _automaticModeLst();
301                         break;
302                 case 0x02:
303                         // display password dialog
304                         DisplayCallKernelSendService(displaySecCmd, NORMAL_FRAME, 13);
305                         break;
306                 case 0x03:
307                         // return back to main menu inside automatic mode.
308                                                 if (_gotoHomeLst != 0)
309                                                         _gotoHomeLst();
310                                                 break;
311                         break;
312 
313                 case 0x04:
314                         // warmup pressed
315                         memcpy(_selectPictureCopy, selectPicture, 8);
316                         //_selectPictureCopy[3] = 0x0A; // successful login
317                         //DisplayCallKernelSendService(_selectPictureCopy, NORMAL_FRAME, 8);
318                         //InitParameterBoard(); // show the values; 
319                                                 if (_startwarmupLst != 0)
320                                                 {
321                                                         _startwarmupLst();
322                                                 }
323                                                 break;
324 
325                 case 0x05:
326                         // return to main menue.
327                         if (_gotoHomeLst != 0)
328                                                         _gotoHomeLst();
329                         break;
330 
331                 case 0x06:
332                         // start button is pressed
333                         // inform the latest parameters
334                         if (_updateKvpLst != 0)
335                         {
336                                 paramVal = GetCommandParameter(displayKVPCmd);
337                                 _updateKvpLst(paramVal);
338                         }
339                         if (_updatemALst != 0)
340                         {
341                                 paramVal = GetCommandParameter(displaymACmd);
342                                 _updatemALst(paramVal);
343                         }
344                         if (_updateTimeLst != 0)
345                         {
346                                 paramVal = GetCommandParameter(displayTimeCmd);
347                                 _updateTimeLst(paramVal);
348                         }
349                         if (_updateJogLst != 0)
350                         {
351                                 paramVal = GetCommandParameter(displayJOGCmd);
352                                 _updateJogLst(paramVal);
353                         }
354                         // start exposure                
355                         if (_startExposureLst != 0)
356                                 _startExposureLst();
357                         break;
358                 case 0x07:
359                         // stop button is pressed
360                         if (_stopExposureLst != 0)
361                                 _stopExposureLst();
362                         break;
363 
364                 case 0x10:
365                         // increase kvp
366                         // TODO: GetCommandParameter is called two times. avoid it!
367 
368                         paramVal = GetCommandParameter(displayKVPCmd);
369                         if ((paramval + KVP_STP) <= MAX_KVP_VAL)
370                         {
371 
372                                 UpdateCommandParameter(displayKVPCmd, KVP_STP);
373                                 ClearKVPBox();
374                                 DisplayKVP();
375                         }
376 
377                         break;
378                 case 0x11:
379                         // increase ma
380                         paramVal = GetCommandParameter(displaymACmd);
381                         if ((paramval + mA_STP) <= MAX_mA)
382                         {
383                                 UpdateCommandParameter(displaymACmd, mA_STP);
384                                 ClearmABox();
385                                 DisplaymA();
386                         }
387                         break;
388                 case 0x12:
389                         // increase time
390                         paramVal = GetCommandParameter(displayTimeCmd);
391                         if ((paramval + Time_STP) <= MAX_Time)
392                         {
393                                 UpdateCommandParameter(displayTimeCmd, Time_STP);
394                                 ClearTimeBox();
395                                 DisplayTime();
396                         }
397                         break;
398                 case 0x13:
399                         //increase jog
400                         paramVal = GetCommandParameter(displayJOGCmd);
401                         if ((paramval + JOG_STP) <= MAX_JOG)
402                         {
403 
404                                 UpdateCommandParameter(displayJOGCmd, JOG_STP);
405                                 ClearJOGBox();
406                                 DisplayJOG();
407                         }
408                         break;
409                 case 0x14:
410                         // decrease kvp
411                         paramVal = GetCommandParameter(displayKVPCmd);
412                         if (MIN_KVP_VAL <= (paramval - KVP_STP))
413                         {
414                                 UpdateCommandParameter(displayKVPCmd, -1 * KVP_STP);
415                                 ClearKVPBox();
416                                 DisplayKVP();
417                         }
418                         break;
419                 case 0x15:
420                         // decrease ma
421                         paramVal = GetCommandParameter(displaymACmd);
422                         if (MIN_mA <= (paramval - mA_STP))
423                         {
424                                 UpdateCommandParameter(displaymACmd, -1 * mA_STP);
425                                 ClearmABox();
426                                 DisplaymA();
427                         }
428                         break;
429                 case 0x16:
430                         // decrease time
431                         paramVal = GetCommandParameter(displayTimeCmd);
432                         if (MIN_Time <= (paramval - Time_STP))
433                         {
434                                 UpdateCommandParameter(displayTimeCmd, -1 * Time_STP);
435                                 ClearTimeBox();
436                                 DisplayTime();
437                         }
438                         break;
439                 case 0x17:
440                         // decrease jog
441                         paramVal = GetCommandParameter(displayJOGCmd);
442                         if (MIN_JOG <= (paramval - JOG_STP))
443                         {
444                                 UpdateCommandParameter(displayJOGCmd, -1 * JOG_STP);
445                                 ClearJOGBox();
446                                 DisplayJOG();
447                         }
448                         break;
449                 }
450                 break;
451         }
452 
453 }
