499 static unsigned char _receiveBuff[40];
500 static int mLen = 0; // message length
501 void DisplayProcessKernelFrame(KERNEL_FRAME frame){
502 
503         
504        _receiveBuff[mLen] = frame.FRAME_MESSAGE[0];
505          mLen++;
506       if (CheckMessageFinished(_receiveBuff, mLen) != 0)
507         {
508            ProcessMessage(_receiveBuff);
509            memset(_receiveBuff, '\0', 40);
510            mLen = 0;
511         }
512 }
