300 void KL_ClearKernelMemory()
301 {
302     int idx = 0;
303     memset(_uart1AccBuf, 0x00, MAX_MSG_SIZE);
304     memset(_uart2AccBuf, 0x00, MAX_MSG_SIZE);
305     memset(_uart3AccBuf, 0x00, MAX_MSG_SIZE);
306     memset(_uart4AccBuf, 0x00, MAX_MSG_SIZE);
307 
308     // initialize IO buffers
309     for (idx = 0; idx < DEVICE_BUFFER_SIZE; idx++)
310     {
311         memset(genSendBuf[idx].pData, '\0', MAX_MSG_SIZE);
312         genSendBuf[idx].iLen = 0;
313 
314         memset(actSendBuf[idx].pData, '\0', MAX_MSG_SIZE);
315         actSendBuf[idx].iLen = 0;
316 
317         memset(wrkSendBuf[idx].pData, '\0', MAX_MSG_SIZE);
318         wrkSendBuf[idx].iLen = 0;
319 
320         memset(disSndBuf[idx].pData, '\0', MAX_MSG_SIZE);
321         disSndBuf[idx].iLen = 0;
322 
323         Uart1ReceiveBuf[idx].MESSAGE_LENGTH = 0;
324         Uart4ReceiveBuf[idx].MESSAGE_LENGTH = 0;
325     }
326 }
