334 void KL_CallSlotHandler(KERNEL_FRAME _frame)
335 {
336 
337 #if build==debug
338     //SendDebugMsg("\r    SEARCHING SLOT...");
339 #endif
340 
341     int i = 0;
342     for (i = 0; i <= __DEVICE_TYPES_COUNT; i++)
343     {
344         // the callback slot is not empty
345         if (_SLOTS[i].device == _frame.F_RECEIVER)
346         {
347             //KERNEL_FRAME _frame;
348 
349 #if build==debug
350             //  SendDebugMsg("\r SLOT FOUND!");
351 #endif
352             //_frame.F_SENDER = device;
353             //_frame.F_TYPE = UNSTRUCTURED; // kernel do not pars the received msg
354             //_frame.FRAME_MESSAGE = message;
355             _SLOTS[i].handler(_frame);
356             break;
357         }
358     }
