394 void KL_DistributeFrames()
395 {
396 
397     int _index = 0;
398 
399     // First Device: GENERATOR
400     // if mutex is free ?
401     if (genSendMutex == false)
402     {
403         // take a pointer to the generator send buffer
404         // DO NOT DIRECTLY USE THE actSendBuf. IT WILL NOT WORK!!!
405         int* xLen = &genSendBuf[genSndRdPtr].iLen;
406         char* xPtr = genSendBuf[genSndRdPtr].pData;
407 
408         // what is the generator message length?
409         if (*xLen == 0)
410         {
411             // contol message length
412             if (strlen(xPtr) != 0)
413             {
414                 // message exists
415                 // send until hit c-style string termination charachter
416 #if build == debug
417                 SendDebugMsg("\r    >>SEND TO GENERATOR:  ");
418 #endif
419 
420                 // send until  hit the C-style string termination charachter
421             // TODO: what if the terminating string was something else?
422                 _index = 0;
423                 while (xPtr[_index] != '\0')
424                 {
425 
426 #if build == debug
427                     SendDebugChr(xPtr[_index]);
428 #endif
429                     SendToGenerator(xPtr[_index]);
430                     _index++;
431                 }
432 
433                 // clear buffer
434                 memset(xPtr, '\0', MAX_MSG_SIZE - 1);
435                 // not necessary for here. protect against any possible trash values
436                 *xLen = 0;
437 
438                 // circular buffer pointer re-allocation
439                 if (genSndRdPtr == DEVICE_BUFFER_SIZE - 1 || genSndRdPtr >= DEVICE_BUFFER_SIZE)
440                 {
441                     genSndRdPtr = 0;  // return to the head
442                 }
443                 else
444                 {
445                     genSndRdPtr = genSndRdPtr + 1; // proceed one step
446                 }
447             }
448         }
449         else
450         {
451             // send message by the amount of its length
452 #if build == debug
453             SendDebugMsg("\r    >>SEND TO GENERATOR:  ");
454 #endif
455             _index = 0;
456             while (_index <= *xLen)
457             {
458 
459 #if build == debug
460                 SendDebugChr(xPtr[_index]);
461 #endif
462                 SendToGenerator(xPtr[_index]);
463                 _index++;
464             }
465 
466             // clear buffer
467             memset(xPtr, '\0', MAX_MSG_SIZE - 1);
468             *xLen = 0;    // this is must!
469 
470             // relocate circular buffer pointer 
471             if (genSndRdPtr == DEVICE_BUFFER_SIZE - 1 || genSndRdPtr >= DEVICE_BUFFER_SIZE)
472             {
473                 genSndRdPtr = 0;  // return to the head
474             }
475             else
476             {
477                 genSndRdPtr = genSndRdPtr + 1;
478             }
479         }
480     }
481 
482     // Second Device: Actuator
483     // if mutex is free?
484     if (actMutex == false)
485     {
486 
487         // take a pointer to the actuator send buffer
488         // DO NOT DIRECTLY USE THE actSendBuf. IT WILL NOT WORK!!!
489         int* xLen = &actSendBuf[actSndRdPtr].iLen;
490         char* xPtr = actSendBuf[actSndRdPtr].pData;
491 
492         //what is the actuator message length?
493         if (*xLen == 0)
494         {
495             // zero length messages: 
496             // a) send til hit C-Style null termination charachter
497             // b) nothing to send.
498             if (strlen(xPtr) != 0)
499             {
500                 // message exists
501                 // send until hit C-Style string termination string
502 #if build == debug
503                 SendDebugMsg("\r    >> SEND TO ACTUATOR :  ");
504 #endif
505                 while (xPtr[_index] != '\0')
506                 {
507 #if build == debug
508                     SendDebugChr(xPtr[_index]);
509 #endif
510                     SendToActuator(xPtr[_index]);
511                     _index++;
512                 }
513 
514                 // clear buffer
515                 memset(xPtr, '\0', MAX_MSG_SIZE - 1);
516                 // not necessary, to make sure not trash value exists
517                 *xLen = 0;
518                 //circular buffer
519                 if (actSndRdPtr == DEVICE_BUFFER_SIZE - 1 || actSndRdPtr >= DEVICE_BUFFER_SIZE)
520                 {
521                     actSndRdPtr = 0;  // return to the head
522                 }
523                 else
524                 {
525                     actSndRdPtr = actSndRdPtr + 1;
526                 }
527             }
528         }
529         else
530         {
531             // sends message by the amount of its length
532 #if build == debug
533             SendDebugMsg("    >> SEND TO ACTUATOR : ");
534 #endif
535             _index = 0;
536             while (_index < *xLen)
537             {
538 #if build == debug
539                 SendDebugChr(xPtr[_index]);
540 #endif
541                 SendToActuator(xPtr[_index]);
542                 _index++;
543             }
544 
545             // clear buffer & message length
546             memset(xPtr, '\0', MAX_MSG_SIZE - 1);
547             *xLen = 0;    // this is a must!
548 
549             if (actSndRdPtr == DEVICE_BUFFER_SIZE - 1 || actSndRdPtr >= DEVICE_BUFFER_SIZE)
550             {
551                 actSndRdPtr = 0;  // return to the head
552             }
553             else
554             {
555                 actSndRdPtr = actSndRdPtr + 1;
556             }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                // relocate circular buffer pointer
557         }
558     }
559 
560 
561     // Third Device: Display
562     // if mutex is free
563     if (disMutex == false)
564     {
565         // control message length
566         if (disSndBuf[disSndRdPtr].iLen == 0)
567         {
568             // Two cases:
569             // a) Send until hit C-Style null terminating char
570             // b) Nothing to send
571             if (strlen((disSndBuf[disSndRdPtr].pData)) != 0)
572             {
573                 // message exists
574                 // send until hit C-Style string termination string
575 
576 #if build == debug
577                 SendDebugMsg("\r    >>DISPLAY MESSAGE:  ");
578 #endif
579                 while (((char*)((disSndBuf[disSndRdPtr]).pData))[_index] != '\0')
580                 {
581 #if build == debug
582                     SendDebugChr(((char*)((disSndBuf[disSndRdPtr]).pData))[_index]);
583 #endif
584                     SendToDisplay(((char*)((disSndBuf[disSndRdPtr]).pData))[_index]);
585                     _index++;
586                 }
587 
588                 // clear buffer
589                 memset(disSndBuf[disSndRdPtr].pData, '\0', MAX_MSG_SIZE);
590                 // not necessary, to make sure no trash value exists
591                 actSendBuf[disSndRdPtr].iLen = 0;
592                 // circular buffer
593                 if (disSndRdPtr == DEVICE_BUFFER_SIZE - 1 || disSndRdPtr >= DEVICE_BUFFER_SIZE)
594                 {
595                     disSndRdPtr = 0;  // return to the head
596                 }
597                 else
598                 {
599                     disSndRdPtr = disSndRdPtr + 1;
600                 }
601             }
602         }
603         else
604         {
605             // send all available chars in message
606 
607             SendDebugMsg("\r    >>SEND TO DISPLAY : ");
608             _index = 0;
609             while (_index < disSndBuf[disSndRdPtr].iLen)
610             {
611 #if build == display
612                 SendDebugChr(((char*)disSndBuf[disSndRdPtr].pData)[_index]);
613 #endif
614                 SendToDisplay(((char*)disSndBuf[disSndRdPtr].pData)[_index]);
615                 _index++;
616             }
617 
618             // clear buffer & message length
619             memset((char*)disSndBuf[disSndRdPtr].pData, '\0', MAX_MSG_SIZE);
620             disSndBuf[disSndRdPtr].iLen = 0;      // this is a must!
621 
622             // relocate circular buffer pointer
623             if (disSndRdPtr == DEVICE_BUFFER_SIZE - 1 || disSndRdPtr >= DEVICE_BUFFER_SIZE)
624             {
625                 disSndRdPtr = 0;  // return to the head
626             }
627             else
628             {
629                 disSndRdPtr = disSndRdPtr + 1;
630             }
631         }
632     }
633 
634 
635     if (uart1ReceiveMutex == false)
636     {
637         if (Uart1ReceiveBuf[Uart1RcivBufRdPtr].MESSAGE_LENGTH != 0)
638         {
639             KL_CallSlotHandler(Uart1ReceiveBuf[Uart1RcivBufRdPtr]);
640 
641             //SendDebugChr(Uart1ReceiveBuf[Uart1RcivBufRdPtr].FRAME_MESSAGE[0]);
642             //SendDebugChr(Uart1ReceiveBuf[Uart1RcivBufRdPtr].FRAME_MESSAGE[1]);
643             // clear buffer
644             //memset( Uart1ReceiveBuf[Uart1RcivBufRdPtr].FRAME_MESSAGE, '\0', Uart1ReceiveBuf[Uart1RcivBufRdPtr].MESSAGE_LENGTH );
645             free(Uart1ReceiveBuf[Uart1RcivBufRdPtr].FRAME_MESSAGE, Uart1ReceiveBuf[Uart1RcivBufRdPtr].MESSAGE_LENGTH);
646             Uart1ReceiveBuf[Uart1RcivBufRdPtr].MESSAGE_LENGTH = 0;
647 
648             // circular buffer pointer re-allocation
649             if (Uart1RcivBufRdPtr == DEVICE_BUFFER_SIZE - 1 || Uart1RcivBufRdPtr >= DEVICE_BUFFER_SIZE)
650             {
651                 Uart1RcivBufRdPtr = 0;  // return to the head
652             }
653             else
654             {
655                 Uart1RcivBufRdPtr = Uart1RcivBufRdPtr + 1; // proceed one step
656             }
657         }
658     }
659     // TODO: implement uart2Receive
660     // TODO: implement uart3Receive
661     if (uart4ReceiveMutex == false)
662     {
663         if (Uart4ReceiveBuf[Uart4RcivBufRdPtr].MESSAGE_LENGTH != 0)
664         {
665             KL_CallSlotHandler(Uart4ReceiveBuf[Uart4RcivBufRdPtr]);
666             free(Uart4ReceiveBuf[Uart4RcivBufRdPtr].FRAME_MESSAGE, Uart4ReceiveBuf[Uart4RcivBufRdPtr].MESSAGE_LENGTH);
667             Uart4ReceiveBuf[Uart4RcivBufRdPtr].MESSAGE_LENGTH = 0;
668 
669             // circular buffer pointer re-allocation
670             if (Uart4RcivBufRdPtr == DEVICE_BUFFER_SIZE - 1 || Uart4RcivBufRdPtr >= DEVICE_BUFFER_SIZE)
671             {
672                 Uart4RcivBufRdPtr = 0;  // return to the head
673             }
674             else
675             {
676                 Uart4RcivBufRdPtr = Uart4RcivBufRdPtr + 1; // proceed one step
677             }
678         }
679     }
680 
681 }
