690 void EnqueueFrameIntoSendBuffer(KL_PACKET buffer[], KERNEL_FRAME* frame, int* index, bool* mutex)
691 {
692     //int i = 0;
693     if (frame->MESSAGE_LENGTH == 0)
694     {
695         *mutex = true;
696         strcpy((char*)buffer[*index].pData, frame->FRAME_MESSAGE); // copy message
697         *mutex = false;
698     }
699     else
700     {
701         *mutex = true;
702         memcpy((char*)(buffer[*index].pData), frame->FRAME_MESSAGE, (*frame)->MESSAGE_LENGTH);// MAX_MSG_SIZE);
703         (buffer[*index]).iLen = (*frame)->MESSAGE_LENGTH;
704         *mutex = false;
705         //SendDebugMsg("\r\r");
706         //for (i = 0; i < frame->MESSAGE_LENGTH; i++)
707         //{
708         //        SendDebugChr(((char*)buffer[*index].pData)[i]);
709         //        //SendDebugChr(frame->FRAME_MESSAGE[i]);
710         //}
711     }
712 
713     // relocate circular buffer pointer
714     if (*index == DEVICE_BUFFER_SIZE - 1 || *index >= DEVICE_BUFFER_SIZE)
715     {
716         *index = 0;  // return to the head 
717     }
718     else
719     {
720         *index = (*index) + 1;
721     }
722 
723     // whithout this delay, the timer can not catch new messages!!!
724     Delay_ms(1);
725 }
726 
734 void EnqueueFrameIntoReceiveBuffer(KERNEL_FRAME buffer[], KERNEL_FRAME* frame, int* index, bool* mutex)
735 {
736     //int i = 0;
737 
738     *mutex = true;
739     buffer[*index]->FRAME_MESSAGE = (char*)malloc((*frame)->MESSAGE_LENGTH + 1); // extra space for null terminating string
740     memcpy(buffer[*index].FRAME_MESSAGE, frame->FRAME_MESSAGE, (*frame)->MESSAGE_LENGTH);
741     (buffer[*index]).MESSAGE_LENGTH = (*frame)->MESSAGE_LENGTH;
742     buffer[*index].F_RECEIVER = frame->F_RECEIVER;
743     buffer[*index].F_TYPE = frame->F_TYPE;
744     buffer[*index]->FRAME_MESSAGE[(*frame)->MESSAGE_LENGTH] = '\0';
745     *mutex = false;
746 
747     //SendDebugMsg("FRAME ENQUEUED:   ");
748     //for (i = 0; i < frame->MESSAGE_LENGTH; i++)
749     //{
750     //        SendDebugChr(buffer[*index].FRAME_MESSAGE[i]);
751     //}
752 
753     // relocate circular buffer pointer
754     if (*index == DEVICE_BUFFER_SIZE - 1 || *index >= DEVICE_BUFFER_SIZE)
755     {
756         *index = 0;  // return to the head 
757     }
758     else
759     {
760         *index = (*index) + 1;
761     }
762 
763     // whithout this delay, the timer can not catch new messages!!!
764     Delay_ms(1);
765 }
