768 void KL_Dispacher(KERNEL_FRAME* frame)
769 {
770     int _index = 0;
771     if (frame->F_PRIORITY == REALTIME_FRAME)
772     {
773 
774         // start execution thread immidiately
775         switch (frame->F_RECEIVER)
776         {
777         case WORKSTATION:
778 #if build == debug
779 
780 #endif
781             break;
782         case GENERATOR:
783 #if build == debug
784             //SendDebugMsg("\r    >>GEN HIGH PRIO:  ");
785 #endif
786             if (frame->MESSAGE_LENGTH != 0)
787             {
788                 // Send by the message length
789                 for (_index = 0; _index < frame->MESSAGE_LENGTH; _index++)
790                 {
791 #if build == debug
792                     //SendDebugChr(frame->FRAME_MESSAGE[_index]);
793 #endif 
794                     SendToGenerator(frame->FRAME_MESSAGE[_index]);
795                 }
796             }
797             else
798             {
799                 // Send til hit null terminating string
800                 _index = 0;
801                 while (frame->FRAME_MESSAGE[_index] != 0)
802                 {
803 #if build == debug
804                     //SendDebugChr(frame->FRAME_MESSAGE[_index]);
805 #endif
806                     SendToGenerator(frame->FRAME_MESSAGE[_index]);
807                     _index++;
808                 }
809 
810             }
811 
812             break;
813         case ACTUATOR:
814 #if build == debug
815             SendDebugMsg("\r    >>ACT HIGH PRIO:  ");
816 #endif
817             if (frame->MESSAGE_LENGTH != 0)
818             {
819                 // send by amount of message length
820                 for (_index = 0; _index < frame->MESSAGE_LENGTH; _index++)
821                 {
822 #if build == debug
823                     SendDebugChr(frame->FRAME_MESSAGE[_index]);
824 #endif 
825                     SendToActuator(frame->FRAME_MESSAGE[_index]);
826                 }
827             }
828             else
829             {
830                 // Send till hit null terminating string
831                 _index = 0;
832                 while (frame->FRAME_MESSAGE[_index] != 0)
833                 {
834 #if build == debug
835                     SendDebugChr(frame->FRAME_MESSAGE[_index]);
836 #endif 
837                     SendToActuator(frame->FRAME_MESSAGE[_index]);
838                     _index++;
839                 }
840             }
841 
842             break;
843         case COLLIMATOR:
844             // TODO: implement later
845             break;
846         case  DETECTOR:
847             // TODO: implement later
848             break;
849         case VENTILATOR:
850             // TODO: implement later
851             break;
852         case ALARM:
853             // TODO: implement later
854             break;
855         case COOLING:
856             // TODO: implement later
857             break;
858         case DISPLAY:
859 #if build == debug
860             SendDebugMsg("\r    >>DISP HIGH PRIO:  ");
861 #endif
862             _index = 0;
863             while (frame->FRAME_MESSAGE[_index] != 0)
864             {
865 #if build == debug
866                 SendDebugChr(frame->FRAME_MESSAGE[_index]);
867 #endif
868                 SendToDisplay(frame->FRAME_MESSAGE[_index]);
869                 _index++;
870             }
871             break;
872         }
873     }
874     else
875     {
876 
877         // enqueue the message into the device buffer
878         switch (frame->F_RECEIVER)
879         {
880         case  GENERATOR:
881             EnqueueFrameIntoSendBuffer(genSendBuf, frame, &genSndWrPtr, &genSendMutex);
882             break;
883         case     ACTUATOR:
884             EnqueueFrameIntoSendBuffer(actSendBuf, frame, &actSndWrPtr, &actMutex);
885             break;
886         case      WORKSTATION:
887             break;
888         case   COLLIMATOR:
889             break;
890         case     DETECTOR:
891             break;
892         case DISPLAY:
893             EnqueueFrameIntoSendBuffer(disSndBuf, frame, &disSndWrPtr, &disMutex);
894             break;
895         }
896     }
897 }
