902 static int u1MsgSize = 0;
904 void KL_UART1MessageReceiveHandler() iv IVT_ADDR_U1RXINTERRUPT {
905 
906     U1RXIF_bit = 0;
907     if (UART1_Data_Ready()) // this is MUST; do not remove it
908     {
909         char _recivedChar = UART1_Read();
910         //SendDebugChr(_recivedChar);
911 
912         // Is UART1 Fixed length message device?
913         if (UART1_MSG_LENGTH != 0)
914         {
915             if (u1MsgSize != UART1_MSG_LENGTH)
916             {
917                 _uart1AccBuf[u1MsgSize] = _recivedChar;
918                 u1MsgSize++;
919             }
920             else
921             {
922 
923                 KERNEL_FRAME _frame;
924                 _frame.F_RECEIVER = UART1MappedDevice;
925                 _frame.F_TYPE = UNSTRUCTURED;
926                 _frame.FRAME_MESSAGE = _uart1AccBuf;
927                 _frame.MESSAGE_LENGTH = u1MsgSize;
928                 EnqueueFrameIntoReceiveBuffer(Uart1ReceiveBuf,
929                     &_frame,
930                     &Uart1RcivBufWrPtr,
931                     &uart1ReceiveMutex);
932                 u1MsgSize = 0;
933                 memset(_uart1AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
934             }
935         }
936         else
937         {
938             // is there any delimiter for uart1?
939             if (UART1_DELIMITER != 0)
940             {
941                 if (_recivedChar != UART1_DELIMITER && u1MsgSize < MAX_MSG_SIZE)
942                 {
943                     _uart1AccBuf[u1MsgSize] = _recivedChar;
944                     u1MsgSize++;
945                 }
946                 else
947                 {
948 
949                     KERNEL_FRAME _frame;
950                     _frame.F_RECEIVER = UART1MappedDevice;
951                     _frame.F_TYPE = UNSTRUCTURED;
952                     _frame.FRAME_MESSAGE = _uart1AccBuf;
953                     _frame.MESSAGE_LENGTH = u1MsgSize;
954                     KL_CallSlotHandler(_frame);
955                     u1MsgSize = 0;
956                     memset(_uart1AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
957                 }
958             }
959             else
960             {
961                 // there is no delimitor nor the messages length is fixed
962                 // so,send the received byte to the device which is mapped on UART1
963                 // the device will decide what to do with that.                                
964                 KERNEL_FRAME _frame;
965                 _uart1AccBuf[0] = _recivedChar;
966                 _frame.F_RECEIVER = UART1MappedDevice;
967                 _frame.F_TYPE = UNSTRUCTURED;
968                 _frame.FRAME_MESSAGE = _uart1AccBuf;
969                 _frame.MESSAGE_LENGTH = 1;
970                 EnqueueFrameIntoReceiveBuffer(Uart1ReceiveBuf,
971                     &_frame,
972                     &Uart1RcivBufWrPtr,
973                     &uart1ReceiveMutex);
974                 u1MsgSize = 0;
975                 memset(_uart1AccBuf, '\0', 1); //clear buffer
976             }
977         }
978 
979         if (U1STA.OERR = 1)
980         {
981             U1STA.OERR = 0;
982             U1STA.FERR = 0;
983         }
984     }
985 }
