987 static int u2MsgSize = 0;
989 void KL_UART2MessageReceiveHandler() iv IVT_ADDR_U2RXINTERRUPT {
990 
991 
992     U2RXIF_bit = 0;
993     if (UART2_Data_Ready()) // this is MUST; do not remove it
994     {
995         char _recivedChar = UART2_Read();
996         //SendDebugChr(_recivedChar);
997         // messages are FIXED length or not?
998         if (UART2_MSG_LENGTH != 0)
999         {
1000             if (u2MsgSize != UART2_MSG_LENGTH)
1001             {
1002                 _uart2AccBuf[u2MsgSize] = _recivedChar;
1003                 u2MsgSize++;
1004             }
1005             else
1006             {
1007                 KERNEL_FRAME _frame;
1008                 _frame.F_RECEIVER = UART2MappedDevice;
1009                 _frame.F_TYPE = UNSTRUCTURED;
1010                 _frame.FRAME_MESSAGE = _uart2AccBuf;
1011                 _frame.MESSAGE_LENGTH = u2MsgSize;
1012                 EnqueueFrameIntoReceiveBuffer(Uart2ReceiveBuf,
1013                     &_frame,
1014                     &Uart2RcivBufWrPtr,
1015                     &uart2ReceiveMutex);
1016                 u2MsgSize = 0;
1017                 memset(_uart2AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
1018             }
1019         }
1020         else
1021         {
1022             // is there any delimiter for uart2?
1023             if (UART2_DELIMITER != 0)
1024             {
1025                 if (_recivedChar != UART2_DELIMITER && u2MsgSize < MAX_MSG_SIZE)
1026                 {
1027                     _uart2AccBuf[u2MsgSize] = _recivedChar;
1028                     u2MsgSize++;
1029                 }
1030                 else
1031                 {
1032                     KERNEL_FRAME _frame;
1033                     _frame.F_RECEIVER = UART2MappedDevice;
1034                     _frame.F_TYPE = UNSTRUCTURED;
1035                     _frame.FRAME_MESSAGE = _uart2AccBuf;
1036                     _frame.MESSAGE_LENGTH = u2MsgSize;
1037                     EnqueueFrameIntoReceiveBuffer(Uart2ReceiveBuf,
1038                         &_frame,
1039                         &Uart2RcivBufWrPtr,
1040                         &uart2ReceiveMutex);
1041                     u2MsgSize = 0;
1042                     memset(_uart2AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
1043                 }
1044 
1045             }
1046             else
1047             {
1048                 // there is no delimitor nor the messages length is fixed
1049                 // so,send the received byte to the device which is mapped on UART2
1050                 // the device will decide what to do with that.
1051                 KERNEL_FRAME _frame;
1052                 _uart2AccBuf[0] = _recivedChar;
1053                 _frame.F_RECEIVER = UART2MappedDevice;
1054                 _frame.F_TYPE = UNSTRUCTURED;
1055                 _frame.FRAME_MESSAGE = _uart2AccBuf;
1056                 _frame.MESSAGE_LENGTH = 1;
1057                 KL_CallSlotHandler(_frame);
1058                 memset(_uart2AccBuf, '\0', MAX_MSG_SIZE);
1059             }
1060         }
1061 
1062         if (U2STA.OERR = 1)
1063         {
1064             U2STA.OERR = 0;
1065             U2STA.FERR = 0;
1066         }
1067     }
1068 
1069 }
