1155 static int u4MsgSize = 0;
1157 void KL_UART4MessageReceiveHandler() iv IVT_ADDR_U4RXINTERRUPT {
1158 
1159 
1160     U4RXIF_bit = 0;
1161     if (UART4_Data_Ready()) // this is MUST; do not remove it
1162     {
1163         char _recivedChar = UART4_Read();
1164         SendDebugChr(_recivedChar);
1165         // messages are FIXED size length or not
1166         if (UART4_MSG_LENGTH != 0)
1167         {
1168             if (u4MsgSize != UART4_MSG_LENGTH)
1169             {
1170                 _uart4AccBuf[u4MsgSize] = _recivedChar;
1171                 u4MsgSize++;
1172             }
1173             else
1174             {
1175                 KERNEL_FRAME _frame;
1176                 _frame.F_RECEIVER = UART4MappedDevice;
1177                 _frame.F_TYPE = UNSTRUCTURED;
1178                 _frame.FRAME_MESSAGE = _uart4AccBuf;
1179                 _frame.MESSAGE_LENGTH = u4MsgSize;
1180                 EnqueueFrameIntoReceiveBuffer(Uart4ReceiveBuf,
1181                     &_frame,
1182                     &Uart4RcivBufWrPtr,
1183                     &uart4ReceiveMutex);
1184                 u4MsgSize = 0;
1185                 memset(_uart4AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
1186             }
1187         }
1188         else
1189         {
1190             // is there any delimitor for uart4?
1191             if (UART4_DELIMITER != 0)
1192             {
1193                 if (_recivedChar != UART4_DELIMITER && u4MsgSize < MAX_MSG_SIZE)
1194                 {
1195                     _uart4AccBuf[u4MsgSize] = _recivedChar;
1196                     u4MsgSize++;
1197                 }
1198                 else
1199                 {
1200                     KERNEL_FRAME _frame;
1201                     _frame.F_RECEIVER = UART4MappedDevice;
1202                     _frame.F_TYPE = UNSTRUCTURED;
1203                     _frame.FRAME_MESSAGE = _uart4AccBuf;
1204                     _frame.MESSAGE_LENGTH = u4MsgSize;
1205                     EnqueueFrameIntoReceiveBuffer(Uart4ReceiveBuf,
1206                         &_frame,
1207                         &Uart4RcivBufWrPtr,
1208                         &uart4ReceiveMutex);
1209                     u4MsgSize = 0;
1210                     memset(_uart4AccBuf, '\0', MAX_MSG_SIZE); //clear buffer
1211                 }
1212 
1213             }
1214             else
1215             {
1216                 // there is no delimitor nor the messages length is fixed
1217                 // send the received byte to the device which is mapped on UART4
1218                 // the device will decide what to do with that.
1219                 // instead of queue-ing the byte, the byte is sent directly
1220                 // to the handler.
1221                 KERNEL_FRAME _frame;
1222                 _uart4AccBuf[0] = _recivedChar;
1223                 _frame.F_RECEIVER = UART4MappedDevice;
1224                 _frame.F_TYPE = UNSTRUCTURED;
1225                 _frame.FRAME_MESSAGE = _uart4AccBuf;
1226                 _frame.MESSAGE_LENGTH = 1;
1227                 KL_CallSlotHandler(_frame);
1228                 u4MsgSize = 0;
1229                 memset(_uart4AccBuf, '\0', 1); //clear buffer
1230             }
1231         }
1232 
1233 
1234         if (U4STA.OERR = 1)
1235         {
1236             U4STA.OERR = 0;
1237             U4STA.FERR = 0;
1238         }
1239     }
1240 
1241 }
