1273 void KL_Bootup()
1274 {
1275     //int idx = 0;
1276     InitializePICSpeed();
1277     InitializePICPins();  // ALWAYS THIS COMES FIRST
1278     InitializeUARTS();    // ALWAYS THIS COMES AFTER
1279     InitializeTimers();
1280     InitializeHeap();
1281     PrioritizeInterrupts();
1282     InitializeBuzzer();
1283 
1284     KL_ClearKernelMemory();
1285 
1286     //Play boot successful beep
1287     Sound_Play(1600, 100);
1288     SendDebugMsg("\r-----------------------");
1289     SendDebugMsg("\r X-Ray Package Ordering & Signal Transmitting (xPOST) kernel.");
1290     SendDebugMsg("\r Version: 0.5");
1291     SendDebugMsg("\r Date: May, 2018");
1292 #if build==debug
1293     SendDebugMsg("\r BOOTUP IN DEBUG MODE.");
1294 #endif
1295     SendDebugMsg("\r-----------------------\r");
1296     Delay_ms(100);
1297     KL_StartDispacherEngine();
1298     _startupFunction();
1299 
1300     while (1)
1301     {
1302         asm nop;
1303     }
1304 
1305 }
